import React ,{ Component } from 'react';
import Particles from 'react-particles-js'
import Nav from './components/nav/nav'  
import Logo from './components/logo/logo'
import Imageform from './components/imageform/imageform'
import Sighin from './components/Sighin/sighin'    
import Clarifai from 'clarifai';
import Facepicture from './components/facepicture/facepicture';
import './App.css';


const app = new Clarifai.App({
 apiKey: '2c21491a673d4d378ab2fa942b61baeb'
});
const particle={
                particles: {
                  line_linked: {
                    shadow: {
                      enable: true,
                      color: "#3CA9D1",
                      blur: 5
                    }
                  }
                }
 }

class App extends Component {
  constructor() {
    super();
    this.state = {
      input:'',
      imageUrl:'',
      box: []
    }
  }

facelocation = (data) =>{
  const clariface = data.outputs[0].data.regions[0].region_info.bounding_box
  const image = document.getElementById('inputimage');
  const width = Number(image.width);
  const height = Number(image.height);
  return {
    leftCol:clariface.left_col * width,
    topRow:clariface.top_row * height,
    rightCol:width-(clariface.right_col*width),
    bottomRow:height-(clariface.bottom_row*height)
  }
}
 drawBoxes =() =>{
  const image = document.getElementById('inputimage');
  $(canvas).attr("width", imageDetails.width).attr("height", imageDetails.height);
  ctx = canvas.getContext("2d");
  ctx.textBaseline = "top";

  for(var i=0; i<imageDetails.clarifaiFaces.length; i++) {
    box = {
      x: imageDetails.clarifaiFaces[i].left_col * imageDetails.width,
      y: imageDetails.clarifaiFaces[i].top_row * imageDetails.height,
      w: (imageDetails.clarifaiFaces[i].right_col * imageDetails.width) - (imageDetails.clarifaiFaces[i].left_col * imageDetails.width),
      h: (imageDetails.clarifaiFaces[i].bottom_row * imageDetails.height) - (imageDetails.clarifaiFaces[i].top_row * imageDetails.height)
    }
    imageDetails.realFaces.push(box);
    ctx.font = (box.w * 1.4) + "px monospace";
    ctx.fillText(overlay, box.x - (box.w / 5), box.y - (box.h/4));
  }
}




displaybox = (box) =>{
  this.setState({box:box})
}
  onInputchange = (event) =>{
   this.setState({input:event.target.value});
  }
  onsubmit = () =>{
    this.setState({imageUrl:this.state.input})
    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input).then(response => this.displaybox(this.facelocation(response))).catch(err=>console.log(err))
 }
   render(){
      return (
        <div className="App">
          <Particles className='particles' params={particle} />
          <Nav />      
          <Logo />
          <Sighin />  
          <Imageform onInputchange={this.onInputchange} onsubmit={this.onsubmit}/>
          <Facepicture box={this.state.box} imageUrl={this.state.imageUrl}/>
        </div>
    );
  }
}

export default App;
