import React from 'react';


class register extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      email:'',
      password:'',
      name:''
    }
  }
  onEmailchange = (event) => {
    this.setState({email:event.target.value})
  }
  onPasswordchange = (event) => {
    this.setState({password:event.target.value})
  }
  onnamechange = (event) => {
    this.setState({name:event.target.value})
  }
  onSubmitsignin = () =>{
    fetch('http://localhost:3000/register',{
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body:JSON.stringify({
        email:this.state.email,
        password:this.state.password,
        name:this.state.name
      })
    })
      .then(response=>response.json())
      .then(user=>{
      if(user){
        this.props.onroutechange('home');
      }
    })
  }
  render(){
    const {onroutechange} = this.props;
      return (  
        <article className="br3 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l shadow-5 mw6 center">
          <main className="pa4 black-80">
            <form className="measure">
              <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                <legend className="f1 fw6 ph0 mh0">Register</legend>
                <div className="mt3">
                  <label className="db fw6 lh-copy f6" htmlFor="email-address">Name</label>
                  <input 
                  className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                  type="text" 
                  name="name"  
                  id="name"
                  onChange={this.onnamechange} 
                  />
                </div>
                <div className="mt3">
                  <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                  <input 
                  className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                  type="email" 
                  name="email-address"  
                  id="email-address"
                  onChange={this.onEmailchange} 
                  />
                </div>
                <div className="mv3">
                  <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                  <input 
                  className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                  type="password" 
                  name="password"  
                  id="password"
                  onChange={this.onPasswordchange} 
                  />
                </div>
              </fieldset>
              <div className="">
                <input onClick={()=>onroutechange('register')} className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Register" />
              </div>
            </form>
          </main>
        </article>
      )
  }
}

export default register;
