import React ,{ Component } from 'react';
import Particles from 'react-particles-js'
import Nav from './components/nav/nav'  
import Logo from './components/logo/logo'
import Imageform from './components/imageform/imageform'
import Sighin from './components/Sighin/sighin' 
import Register from './components/register/register'   
import Clarifai from 'clarifai';
import Facepicture from './components/facepicture/facepicture';
import './App.css';


const app = new Clarifai.App({
 apiKey: '2c21491a673d4d378ab2fa942b61baeb'
});
const particle={
                particles: {
                  line_linked: {
                    shadow: {
                      enable: true,
                      color: "#3CA9D1",
                      blur: 5
                    }
                  }
                }
 }

class App extends Component {
  constructor() {
    super();
    this.state = {
      input:'',
      imageUrl:'',
      box: {},
      route:'sighin',
      issighin:false,
      user:{
        id:'',
        name:'',
        email:'',
        entries:0,
        joined:''
      }
    }
  }
  loadUser =(data)=>{
    this.setState({user:{
        id:data.id,
        name:data.name,
        email:data.email,
        entries:data.entries,
        joined:data.joined
    }})
  }

facelocation = (data) =>{
  const clariface = data.outputs[0].data.regions[0].region_info.bounding_box
  const image = document.getElementById('inputimage');
  const width = Number(image.width);
  const height = Number(image.height);
  return {
    leftCol:clariface.left_col * width,
    topRow:clariface.top_row * height,
    rightCol:width-(clariface.right_col*width),
    bottomRow:height-(clariface.bottom_row*height)
  }
}

onroutechange = (route) =>{
  if(route === 'sighout'){
    this.setState({issighin:false})
  } else if(route === 'home'){
    this.setState({issighin:true})
  }
  this.setState({route:route})
}

displaybox = (box) =>{
  this.setState({box:box})
}
  onInputchange = (event) =>{
   this.setState({input:event.target.value});
  }
  onsubmit = () =>{
    this.setState({imageUrl:this.state.input})
    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input).then(response => this.displaybox(this.facelocation(response))).catch(err=>console.log(err))
 }
   render(){
      return (
        <div className="App">
          <Particles className='particles' params={particle} />
          <Nav onroutechange={this.onroutechange} issighin={this.state.issighin}/>
          {  this.state.route==='home'
            ?<div> 
                <Logo />
                <Imageform onInputchange={this.onInputchange} onsubmit={this.onsubmit}/>
                <Facepicture box={this.state.box} imageUrl={this.state.imageUrl}/>
              </div>  
           :(
              this.state.route ==='sighin'
              ?<Sighin onroutechange={this.onroutechange}/>
              :<Register loadUser={ this.loadUser} onroutechange={this.onroutechange}/>
            )
          }
        </div>
    );
  }
}

export default App;
